package test.cfca.sadk.testdata;

import java.io.FileNotFoundException;
import java.io.FileOutputStream;
import java.io.IOException;
import java.security.Key;

import javax.crypto.spec.SecretKeySpec;

import cfca.sadk.algorithm.common.CBCParam;
import cfca.sadk.algorithm.common.Mechanism;

import com.sun.org.apache.xerces.internal.impl.dv.util.Base64;

public final class SYMTestData {

    static public final String dataText = "";
    static public final String desIVText = "MTIzNDU2Nzg=";
    static public final String desKeyText = "tBIngpEmSN+WIyqI9eH/qQ/ut52CC+Gt";
    static public final String desEncryptedText = "";
    static public final String desDecryptedText = "";
    static public final String rc4KeyText = "g7FPxZMSP51MOD4c31JZlg==";
    static public final String rc4EncryptedText = "";
    static public final String rc4DecryptedText = "";
    static public final String sm4IvText = "MTIzNDU2NzhhYmNkZWZnaA==";
    static public final String sm4KeyText = "RgfQ6s8rAwl6kVt5xJp/eA==";
    static public final String sm4EncryptedText = "";
    static public final String sm4DecryptedText = "";

    static public byte[] dataBytes;
    static public byte[] desIVBytes;
    static public byte[] desKeyBytes;
    static public byte[] desEncryptedBytes;
    static public byte[] desDecryptedBytes;
    static public byte[] rc4KeyBytes;
    static public byte[] rc4EncryptedBytes;
    static public byte[] rc4DecryptedBytes;
    static public byte[] sm4IvBytes;
    static public byte[] sm4KeyBytes;
    static public byte[] sm4EncryptedBytes;
    static public byte[] sm4DecryptedBytes;

    static public Key desKey;
    static public Key rc4Key;
    static public Key sm4Key;
    static public final CBCParam desIV = new CBCParam();
    static public final CBCParam sm4IV = new CBCParam();

    static public final Mechanism desMechanism = new Mechanism(Mechanism.DES3_CBC);
    static public final Mechanism rc4Mechanism = new Mechanism(Mechanism.RC4);
    static public final Mechanism sm4Mechanism = new Mechanism(Mechanism.SM4_CBC);

    static {
        dataBytes = Base64.decode(dataText);
        desIVBytes = Base64.decode(desIVText);
        desKeyBytes = Base64.decode(desKeyText);
        desEncryptedBytes = Base64.decode(desEncryptedText);
        desDecryptedBytes = Base64.decode(desDecryptedText);
        desKey = new SecretKeySpec(desKeyBytes, "DESede");
        desIV.setIv(desIVBytes);
        desMechanism.setParam(desIV);

        rc4KeyBytes = Base64.decode(rc4KeyText);
        rc4EncryptedBytes = Base64.decode(rc4EncryptedText);
        rc4DecryptedBytes = Base64.decode(rc4DecryptedText);
        rc4Key = new SecretKeySpec(rc4KeyBytes, "RC4");

        sm4IvBytes = Base64.decode(sm4IvText);
        sm4KeyBytes = Base64.decode(sm4KeyText);
        sm4EncryptedBytes = Base64.decode(sm4EncryptedText);
        sm4DecryptedBytes = Base64.decode(sm4DecryptedText);
        sm4Key = new SecretKeySpec(sm4KeyBytes, "SM4");
        sm4IV.setIv(sm4IvBytes);
        sm4Mechanism.setParam(sm4IV);

        boolean writeFile = false;
        if (writeFile) {
            try {
                FileOutputStream fos = new FileOutputStream("TestData/sym/test.dat");
                fos.write(dataBytes);
                fos.close();
                fos = new FileOutputStream("TestData/sym/desEncryptedBytes.dat");
                fos.write(desEncryptedBytes);
                fos.close();
                fos = new FileOutputStream("TestData/sym/rc4EncryptedBytes.dat");
                fos.write(rc4EncryptedBytes);
                fos.close();
                fos = new FileOutputStream("TestData/sym/sm4EncryptedBytes.dat");
                fos.write(sm4EncryptedBytes);
                fos.close();
            } catch (FileNotFoundException e) {
                e.printStackTrace();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }

    }

}
